import React, { Component } from 'react'

import api from '../api'
const ReactTable = window.ReactTable.default;


import styled from 'styled-components'

import 'react-table/react-table.css'

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

const Update = styled.div`
    color: #ef9b0f;
    cursor: pointer;
`

const Delete = styled.div`
    color: #ff0000;
    cursor: pointer;
`

class UpdateTicket extends Component {
    updateUser = event => {
        event.preventDefault()

        window.location.href = `/ticket/update/${this.props.id}`
    }

    render() {
        return <Update onClick={this.updateUser}>Update</Update>
    }
}

class DeleteTicket extends Component {
    deleteUser = event => {
        event.preventDefault()

        if (
            window.confirm(
                `Do tou want to delete the ticket ${this.props.id} permanently?`,
            )
        ) {
            api.deleteTicketById(this.props.id)
            window.location.reload()
        }
    }

    render() {
        return <Delete onClick={this.deleteUser}>Delete</Delete>
    }
}

class TicketList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            ticket: [],
            columns: [],
            isLoading: false,
        }
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        await api.getAllTicket().then(ticket => {
            this.setState({
                ticket: ticket.data.data,
                isLoading: false,
            })
        })
    }

    render() {
        const { ticket, isLoading } = this.state

        const columns = [
            {
                Header: 'ID',
                accessor: '_id',
                filterable: true,
            },
            {
                Header: 'Nomcommerce',
                accessor: 'name',
                filterable: true,
            },
            {
                Header: 'Produit',
                accessor: 'product',
                filterable: true,
            },
	    {
                Header: 'Prix',
                accessor: 'price',
                filterable: true,
            },
            {
                Header: 'date',
                accessor: 'time',
                Cell: props => <span>{props.value.join(' / ')}</span>,
            },
            {
                Header: '',
                accessor: '',
                Cell: function(props) {
                    return (
                        <span>
                            <DeleteTicket id={props.original._id} />
                        </span>
                    )
                },
            },
            {
                Header: '',
                accessor: '',
                Cell: function(props) {
                    return (
                        <span>
                            <UpdateTicket id={props.original._id} />
                        </span>
                    )
                },
            },
        ]

        let showTable = true
        if (!ticket.length) {
            showTable = false
        }

        return (
            <Wrapper>
                {showTable && (
                    <ReactTable
                        data={ticket}
                        columns={columns}
                        loading={isLoading}
                        defaultPageSize={10}
                        showPageSizeOptions={true}
                        minRows={0}
                    />
                )}
            </Wrapper>
        )
    }
}

export default TicketList
