import React, { Component } from 'react'
import api from '../api'

import styled from 'styled-components'
import Tesseract from 'tesseract.js'

const Title = styled.h1.attrs({
    className: 'h1',
})``

const Wrapper = styled.div.attrs({
    className: 'form-group',
})`
    margin: 0 30px;
`

const Label = styled.label`
    margin: 5px;
`

const InputText = styled.input.attrs({
    className: 'form-control',
})`
    margin: 5px;
`

const Button = styled.button.attrs({
    className: `btn btn-primary`,
})`
    margin: 15px 15px 15px 5px;
`

const CancelButton = styled.a.attrs({
    className: `btn btn-danger`,
})`
    margin: 15px 15px 15px 5px;
`

class TicketInsert extends Component {
    constructor(props) {
        super(props)

        this.state = {
            nomcommerce: '',
            produits: '',
            total: '',
	    date: '',
	    uploads: [],
	    patterns: [],
	    documents: []
        }
    }

    handleChangeInputNomcommerce = async event => {
        const nomcommerce = event.target.value
        this.setState({ nomcommerce })
    }

    handleChangeInputProduits = async event => {
        const produits = event.target.value
        this.setState({ produits })
    }

    handleChangeInputTotal = async event => {
        const total = event.target.validity.valid
            ? event.target.value
            : this.state.total

        this.setState({ total })
    }

    handleChangeInputDate = async event => {
        const date = event.target.value
        this.setState({ date })
    }

    handleIncludeTicket = async () => {
        const { nomcommerce, produits, total, date } = this.state
        const arrayTime = date.split('/')
        const payload = { nomcommerce, produits, total, date: arrayTime }

        await api.insertTicket(payload).then(res => {
            window.alert(`Ticket inserted successfully`)
            this.setState({
                nomcommerce: '',
                produits: '',
                total: '',
		date: '',
            })
        })
    }

    handleChange = (event) => {
	if (event.target.files[0]) {
	    var uploads = []
	    for (var key in event.target.files) {
	      if (!event.target.files.hasOwnProperty(key)) continue;
	      let upload = event.target.files[key]
	      uploads.push(URL.createObjectURL(upload))
	    }
	    this.setState({
	      uploads: uploads
	    })
	  } else {
	    this.setState({
	      uploads: []
	    })
	  }
	}

generateText = () => {
  let uploads = this.state.uploads

  for(var i = 0; i < uploads.length; i++) {
    Tesseract.recognize(uploads[i], {
      lang: 'fr'
    })
    .catch(err => {
      console.error(err)
    })
    .then(result => {
      // Get Confidence score
      let confidence = result.confidence

      // Get full output
      let text = result.text

      // Get codes
      let pattern = /\b\w{10,10}\b/g
      let patterns = result.text.match(pattern);

      // Update state
      this.setState({ 
        patterns: this.state.patterns.concat(patterns),
        documents: this.state.documents.concat({
          pattern: patterns,
          text: text,
          confidence: confidence
        })
      })
    })
  }
}




    render() {
        const { nomcommerce, produits, total, date } = this.state
        return (
            <Wrapper>
                <Title>Create Ticket</Title>

                <Label>Nom de Commerce: </Label>
                <InputText
                    type="text"
                    value={nomcommerce}
                    onChange={this.handleChangeInputNomcommerce}
                />

		<Label>Produits: </Label>
		<InputText
                    type="text"
                    value={produits}
                    onChange={this.handleChangeInputProduits}
                />

                <Label>Total: </Label>
                <InputText
                    type="number"
                    step="0.1"
                    min="0"
                    max="100000"
                    pattern="[0-9]+([,\.][0-9]+)?"
                    value={total}
                    onChange={this.handleChangeInputTotal}
                />

                <Label>Date: </Label>
                <InputText
                    type="text"
                    value={date}
                    onChange={this.handleChangeInputDate}
                />

                <Button onClick={this.handleIncludeTicket}>Add Ticket</Button>
                <CancelButton href={'/tickets/list'}>Cancel</CancelButton>


<div className="app">

          <h1>OCR</h1>


        { /* File uploader */ }
        <section className="hero">
  	  <label className="fileUploaderContainer">
    		upload tickets
    		<input type="file" id="fileUploader" onChange={this.handleChange} multiple />
  	  </label>

  	<div>
    	   { this.state.uploads.map((value, index) => {
      		return <img key={index} src={value} width="100px" />
    	   }) }
  	</div>

	<button onClick={this.generateText} className="button">Generate</button>
	</section>

        { /* Results */ }
	<section className="results">
	  { this.state.documents.map((value, index) => {
	    return (
	      <div key={index} className="results__result">
		<div className="results__result__image">
		  <img src={this.state.uploads[index]} width="250px" />
		</div>
		<div className="results__result__info">
		  <div className="results__result__info__codes">
		    <small><strong>Confidence Score:</strong> {value.confidence}</small>
		  </div>
		  <div className="results__result__info__codes">
		    <small><strong>Pattern Output:</strong> {value.pattern.map((pattern) => { return pattern + ', ' })}</small>
		  </div>
		  <div className="results__result__info__text">
		    <small><strong>Full Output:</strong> {value.text}</small>
		  </div>
		</div>
	      </div>
	    )
	  }) }
	</section>
</div>
</Wrapper>
        )
    }
}

export default TicketInsert
