import React, { Component } from 'react'
import ReactTable from 'react-table'
import api from '../api'

import styled from 'styled-components'

import 'react-table/react-table.css'

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

const Update = styled.div`
    color: #ef9b0f;
    cursor: pointer;
`

const Delete = styled.div`
    color: #ff0000;
    cursor: pointer;
`

class UpdateTicket extends Component {
    updateUser = event => {
        event.preventDefault()

        window.location.href = `/tickets/update/${this.props.id}`
    }

    render() {
        return <Update onClick={this.updateUser}>Update</Update>
    }
}

class DeleteTicket extends Component {
    deleteUser = event => {
        event.preventDefault()

        if (
            window.confirm(
                `Do tou want to delete the ticket ${this.props.id} permanently?`,
            )
        ) {
            api.deleteTicketById(this.props.id)
            window.location.reload()
        }
    }

    render() {
        return <Delete onClick={this.deleteUser}>Delete</Delete>
    }
}

class TicketList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            tickets: [],
            columns: [],
            isLoading: false,
        }
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        await api.getAllTicket().then(tickets => {
            this.setState({
                tickets: tickets.data.data,
                isLoading: false,
            })
        })
    }

    render() {
        const { tickets, isLoading } = this.state
        console.log('TCL: TicketList -> render -> tickets', tickets)

        const columns = [
            {
                Header: 'ID',
                accessor: '_id',
                filterable: true,
            },
            {
                Header: 'nomcommerce',
                accessor: 'nomcommerce',
                filterable: true,
            },
            {
                Header: 'produits',
                accessor: 'produits',
                filterable: true,
            },
            {
                Header: '',
                accessor: 'time',
		filterable: true,
            },
            {
                Header: '',
                accessor: '',
                Cell: function(props) {
                    return (
                        <span>
                            <UpdateTicket id={props.original._id} />
                        </span>
                    )
                },
            },
        ]

        let showTable = true
        if (!tickets.length) {
            showTable = false
        }

        return (
            <Wrapper>
                {showTable && (
                    <ReactTable
                        data={tickets}
                        columns={columns}
                        loading={isLoading}
                        defaultPageSize={10}
                        showPageSizeOptions={true}
                        minRows={0}
                    />
                )}
            </Wrapper>
        )
    }
}

export default TicketList
